import React, { useState, useMemo } from 'react';
import './App.css';
import { campaignList } from './campaignData'; 
import Navbar from './components/Navbar';
import FilterTabs from './components/FilterTabs';
import CardGrid from './components/CardGrid';

const isSameDay = (d1, d2) => {
  return d1.getFullYear() === d2.getFullYear() &&
         d1.getMonth() === d2.getMonth() &&
         d1.getDate() === d2.getDate();
};

function App() {
  const [activeTab, setActiveTab] = useState('upcoming');

  const filteredCampaigns = useMemo(() => {
    // FIX: Move 'today' inside the hook
    const today = new Date(); 

    return campaignList.filter(campaign => {
      const campaignDate = new Date(campaign.createdOn);

      if (activeTab === 'upcoming') {
        // Also check that it's not the same day
        return campaignDate > today && !isSameDay(campaignDate, today);
      }
      if (activeTab === 'live') {
        return isSameDay(campaignDate, today);
      }
      if (activeTab === 'past') {
        return campaignDate < today && !isSameDay(campaignDate, today);
      }
      return false;
    });
  // FIX: 'today' is no longer a dependency, only 'activeTab'
  }, [activeTab]); 

  return (
    <div className="app-container">
      <Navbar />

      <main className="content-area">
        <h1 className="main-title">Manage Campaigns</h1>

        <FilterTabs activeTab={activeTab} onTabChange={setActiveTab} />

        <CardGrid campaigns={filteredCampaigns} />
      </main>
    </div>
  );
}

export default App;
