import React from 'react';
import './CampaignCard.css';

const formatDate = (timestamp) => {
  const date = new Date(timestamp);
  return date.toLocaleDateString("en-US", {
    month: "short",
    day: "numeric",
    year: "numeric"
  });
};

const CampaignCard = ({ campaign }) => {
  return (
    <article className="campaign-card">
      <header className="card-header">
        <div className="card-date">{formatDate(campaign.createdOn)}</div>
        <img src={campaign.image_url} alt={campaign.name} className="card-image" />
      </header>

      <section className="card-body">
        <h3 className="card-title">{campaign.name}</h3>
        <p className="card-region">{campaign.region}</p>
      </section>

      <footer className="card-footer">
        <button className="action-button">
          View Pricing
        </button>
        <button className="action-button">CSV</button>
        <button className="action-button">Report</button>
        <button className="action-button">Schedule Again</button>
      </footer>
    </article>
  );
};

export default CampaignCard;
